require 'spec_helper'

describe Bitcoin::Message::CmpctBlock do

  describe '#parse_from_payload' do
    subject {
      Bitcoin::Message::CmpctBlock.parse_from_payload(''.htb)
    }
    it 'should be parsed.' do
      expect(subject.header_and_short_ids.header.hash).to eq('000000004c4bbd3afa5c537669f2b9d167015e220c152c53b3dc5ad58c7c5486')
      expect(subject.header_and_short_ids.header.to_payload.bth).to eq('0000002033781a817676c59166524c3a69b9fd3a9b2a6ed044ebdf7f4c040000000000000fbd4c2e418d224f14a9d7a98b7e1f209c2a0ab012753de877f7af00b73b5a65590e745affff001d2812f96d')
      expect(subject.header_and_short_ids.nonce).to eq(7907921748630648002)
      expect(subject.header_and_short_ids.shortids.size).to eq(116)
      expect(subject.header_and_short_ids.prefilledtxn.size).to eq(1)
      expect(subject.header_and_short_ids.prefilledtxn.first.index).to eq(0)
      expect(subject.header_and_short_ids.prefilledtxn.first.tx.txid).to eq('dc45f5df3946987d1c12f64a58dc7e76882c81e220d933ea13394f1dce425831')
      expect(subject.to_payload.bth).to eq('')
    end
  end

end
