require 'spec_helper'

describe Bitcoin::Message::HeaderAndShortIDs do

  describe '#short_id' do
    subject {
      Bitcoin::Message::HeaderAndShortIDs.parse_from_payload(''.htb)
    }
    it 'should calculate transaction short id.' do
      expect(subject.short_id('b94afd3ad8930daa9bee6db6cfb77cb08f981385966c8993fd5b1b0c589d5b14')).to eq(103525679137205)
      expect(subject.short_id('9d561dbcb898213138ab09154da4f65d8e1557dae3c260e76e0d120543693f1b')).to eq(96102457628354)
      expect(subject.short_id('ec7681ae9ac602c187efa6f4467167e981a5015f23253baaabefcf7176f040a9')).to eq(106338193585143)
    end
  end

end