require 'spec_helper'

describe Bitcoin::PartiallySignedTx do

  # https://github.com/bitcoin/bips/blob/master/bip-0174.mediawiki#Test_Vectors

  it 'valid PSBT' do
    # PSBT with one P2PKH input which has a non-final scriptSig.
    psbt = Bitcoin::PartiallySignedTx.parse_from_payload(''.htb)
    expect(psbt.use_in_index).to be false
    expect(psbt.tx.in.size).to eq(1)
    expect(psbt.partial_inputs.size).to eq(1)
    expect(psbt.partial_inputs[0].index).to eq(0)
    expect(psbt.to_payload.bth).to eq('')

    # PSBT with one P2PKH input which has a non-final scriptSig and has a sighash type specified.
    psbt = Bitcoin::PartiallySignedTx.parse_from_payload(''.htb)
    expect(psbt.use_in_index).to be false
    expect(psbt.tx.in.size).to eq(1)
    expect(psbt.partial_inputs.size).to eq(1)
    expect(psbt.partial_inputs[0].index).to eq(0)
    expect(psbt.partial_inputs[0].sighash_type).not_to be nil
    expect(psbt.partial_inputs[0].non_witness_utxo).not_to be nil
    expect(psbt.partial_inputs[0].witness_utxo).to be nil
    expect(psbt.to_payload.bth).to eq('')

    # PSBT with one P2PKH input and one P2SH-P2WPKH input both with non-final scriptSigs. P2SH-P2WPKH input's redeemScript is available.
    psbt = Bitcoin::PartiallySignedTx.parse_from_payload(''.htb)
    expect(psbt.use_in_index).to be false
    expect(psbt.tx.in.size).to eq(2)
    expect(psbt.partial_inputs.size).to eq(2)
    expect(psbt.partial_inputs[0].index).to eq(0)
    expect(psbt.partial_inputs[0].sighash_type).to be nil
    expect(psbt.partial_inputs[1].index).to eq(1)
    expect(psbt.partial_inputs[1].sighash_type).to be nil
    expect(psbt.partial_inputs[1].non_witness_utxo).to be nil
    expect(psbt.partial_inputs[1].witness_utxo).not_to be nil
    expect(psbt.to_payload.bth).to eq('')

    # PSBT with one P2PKH input and one P2SH-P2WPKH input with only the first input signed and finalized.
    psbt = Bitcoin::PartiallySignedTx.parse_from_payload(''.htb)
    expect(psbt.tx.in.size).to eq(2)
    expect(psbt.tx.in[0].script_sig).not_to be nil
    expect(psbt.partial_inputs.size).to eq(2)
    expect(psbt.partial_inputs[0].index).to eq(0)
    expect(psbt.partial_inputs[0].sighash_type).to be nil
    expect(psbt.partial_inputs[1].index).to eq(1)
    expect(psbt.partial_inputs[1].sighash_type).to be nil
    expect(psbt.partial_inputs[1].non_witness_utxo).to be nil
    expect(psbt.partial_inputs[1].witness_utxo).not_to be nil
    expect(psbt.to_payload.bth).to eq('')

    # PSBT with one P2PKH input and one P2SH-P2WPKH input with only the first input signed, finalized, and skipped. Input indexes are used.
    psbt = Bitcoin::PartiallySignedTx.parse_from_payload(''.htb)
    expect(psbt.use_in_index).to be true
    expect(psbt.tx.in.size).to eq(2)
    expect(psbt.tx.in[0].script_sig).not_to be nil
    expect(psbt.partial_inputs.size).to eq(2)
    expect(psbt.partial_inputs[0].index).to eq(0)
    expect(psbt.partial_inputs[0].sighash_type).to be nil
    expect(psbt.partial_inputs[1].use_in_index).to be true
    expect(psbt.partial_inputs[1].index).to eq(1)
    expect(psbt.partial_inputs[1].sighash_type).to be nil
    expect(psbt.partial_inputs[1].non_witness_utxo).to be nil
    expect(psbt.partial_inputs[1].witness_utxo).not_to be nil
    expect(psbt.to_payload.bth).to eq('')

    # PSBT with one P2SH-P2WSH input of a 2-of-2 multisig, redeemScript, witnessScript, and keypaths are available. Contains one signature.
    psbt = Bitcoin::PartiallySignedTx.parse_from_payload(''.htb)
    expect(psbt.use_in_index).to be false
    expect(psbt.tx.in.size).to eq(1)
    expect(psbt.redeem_scripts.size).to eq(1)
    expect(psbt.witness_scripts.size).to eq(1)
    expect(psbt.hd_key_paths).not_to be nil
    expect(psbt.tx.in[0].script_sig).not_to be nil
    expect(psbt.partial_inputs.size).to eq(1)
    expect(psbt.partial_inputs[0].index).to eq(0)
    expect(psbt.partial_inputs[0].sighash_type).to be nil
    expect(psbt.partial_inputs[0].use_in_index).to be false
    expect(psbt.partial_inputs[0].non_witness_utxo).not_to be nil
    expect(psbt.partial_inputs[0].witness_utxo).to be nil
    expect(psbt.to_payload.bth).to eq('')
  end

  it 'invalid PSBT' do
    # invalid magic
    expect{Bitcoin::PartiallySignedTx.parse_from_payload('0200000001268171371edff285e9not_to be '.htb)}.to raise_error(ArgumentError, 'Invalid PSBT magic bytes.')
    # missing null terminator
    expect{Bitcoin::PartiallySignedTx.parse_from_payload(''.htb)}.to raise_error(ArgumentError, 'Missing null terminator.')
    # Input index is specified but total input count is not given.
    expect{Bitcoin::PartiallySignedTx.parse_from_payload(''.htb)}.to raise_error(ArgumentError, 'Inputs provided does not match the number of inputs stated.')
    # Total input count is given but second input does not have its index.
    expect{Bitcoin::PartiallySignedTx.parse_from_payload(''.htb)}.to raise_error(ArgumentError, 'Inputs provided does not match the number of inputs in transaction.')
  end

  it 'combine PSBT' do
    # input[0] P2SH(multisig)
    redeem_script1 = Bitcoin::Script.parse_from_payload('522103c8727ce35f1c93eb0be21406ee9a923c89219fe9c9e8504c8314a6a22d1295c02103c74dc710c407d7db6e041ee212d985cd2826d93f806ed44912b9a1da691c977352ae'.htb)
    # input[1] P2SH-P2WSH(multisig)
    redeem_script2 = Bitcoin::Script.parse_from_payload('0020a8f44467bf171d51499153e01c0bd6291109fc38bd21b3c3224c9dc6b57590df'.htb)
    witness_script = Bitcoin::Script.parse_from_payload('522102e80dec31d167865c1685e9d7a9291e66a4ea22c65cfee324289a1667ccda3b87210258cbbc3cb295a8bebac233aadc7773978804993798be5390ab444f6dd4c5327e52ae'.htb)

    # creator creates psbt
    tx = Bitcoin::Tx.parse_from_payload('02000000022e8c7d8d37c427e060ec002ec1c2bc30196fc2f75d6a8844cbc03651c081430a0100000000ffffffff96a04e0cc636f377933e3d93accc627faacdbcdb5a9624df1b490bd045f24d2c0000000000ffffffff01e02be50e0000000017a914b53bb0dc1db8c8d803e3e39f784d42e4737ffa0d8700000000'.htb)
    creator_psbt = Bitcoin::PartiallySignedTx.new(tx)
    # set redeem script
    creator_psbt.redeem_scripts[redeem_script1.to_hash160] = redeem_script1.to_payload
    creator_psbt.redeem_scripts[redeem_script2.to_hash160] = redeem_script2.to_payload
    # set witness script
    creator_psbt.witness_scripts[witness_script.to_sha256] = witness_script.to_payload
    # set partial input
    creator_psbt.partial_inputs << Bitcoin::PartiallySignedInput.new(
        non_witness_utxo: Bitcoin::Tx.parse_from_payload(''.htb))
    creator_psbt.partial_inputs << Bitcoin::PartiallySignedInput.new(
        witness_utxo: Bitcoin::TxOut.new(value: 200000000, script_pubkey: Bitcoin::Script.parse_from_payload('a914f3ba8a120d960ae07d1dbe6f0c37fb4c926d76d587'.htb)))

    expect(creator_psbt.to_payload.bth).to eq('')

    # signer1 create psbt
    priv10 = Bitcoin::Key.from_wif('cQxozhqme9dcDbxT97uDu1P32Cnywc5nAMhPtQwyWhVgQnP43WGH')
    priv11 = Bitcoin::Key.from_wif('cP3ArXq5BpHE94R4buJ5uma4pyKvaWXUd5Bpsy3hS2zA69X9KMnM')

    signer1_psbt = Bitcoin::PartiallySignedTx.parse_from_payload(creator_psbt.to_payload)

    partial_in10 = signer1_psbt.partial_inputs[0]
    sighash10 = signer1_psbt.tx.sighash_for_input(0, redeem_script1)
    partial_in10.partial_sigs[priv10.pubkey] = priv10.sign(sighash10) + [Bitcoin::SIGHASH_TYPE[:all]].pack('C')

    partial_in11 = signer1_psbt.partial_inputs[1]
    sighash11 = signer1_psbt.tx.sighash_for_input(1, witness_script, amount: partial_in11.witness_utxo.value, sig_version: :witness_v0)
    partial_in11.partial_sigs[priv11.pubkey] = priv11.sign(sighash11) + [Bitcoin::SIGHASH_TYPE[:all]].pack('C')

    expect(signer1_psbt.to_payload.bth).to eq('')

    # signer2 create psbt
    priv20 = Bitcoin::Key.from_wif('cUL8UxFiJjnLkkZwmmXDxmaNRQfEMDP44eZnSaiYR3KUJNv82chM')
    priv21 = Bitcoin::Key.from_wif('cNQm3eSF9rQnpoUB8xThUVDfaeRVEckPK11rGB6LjweFdhwcCS4A')

    signer2_psbt = Bitcoin::PartiallySignedTx.parse_from_payload(creator_psbt.to_payload)

    partial_in20 = signer2_psbt.partial_inputs[0]
    sighash20 = signer2_psbt.tx.sighash_for_input(0, redeem_script1)
    partial_in20.partial_sigs[priv20.pubkey] = priv20.sign(sighash20) + [Bitcoin::SIGHASH_TYPE[:all]].pack('C')

    partial_in21 = signer2_psbt.partial_inputs[1]
    sighash21 = signer2_psbt.tx.sighash_for_input(1, witness_script, amount: partial_in21.witness_utxo.value, sig_version: :witness_v0)
    partial_in21.partial_sigs[priv21.pubkey] = priv21.sign(sighash21) + [Bitcoin::SIGHASH_TYPE[:all]].pack('C')

    expect(signer2_psbt.to_payload.bth).to eq('')

    # combiner create psbt
    combiner_psbt = signer1_psbt.combine(signer2_psbt)
    expect(combiner_psbt.to_payload.bth).to eq('')

    # finalizer create complete tx
    expect(combiner_psbt.finalize.to_payload.bth).to eq('')
  end

  it 'exist unknown key-value pair' do
    # psbt has global unknown
    psbt = Bitcoin::PartiallySignedTx.parse_from_payload(''.htb)
    expect(psbt.unknowns['056345200f68d189e1adc0df1c4d16ea8f14c0dbeb'].bth).to eq('0020771fd18ad459666dd49f3d564e3dbc42f4c84774e360ada16816a8ed488d5681')
    expect(psbt.to_payload.bth).to eq('')

    # psbt has input unknown
    psbt = Bitcoin::PartiallySignedTx.parse_from_payload(''.htb)
    expect(psbt.partial_inputs.last.unknowns['056345200f68d189e1adc0df1c4d16ea8f14c0dbeb'].bth).to eq('0020771fd18ad459666dd49f3d564e3dbc42f4c84774e360ada16816a8ed488d5681')
    expect(psbt.to_payload.bth).to eq('')
  end
end